urlset:
{% for page in site.pages %}
  {% unless page.url == '/404.html' or page.url == '/sitemap.xml' %}
    {% assign stripped_url = page.url %}
    {% if stripped_url.last == '/' %}
      {% assign stripped_url = stripped_url | remove_last: '/' %}
    {% endif %}

      {{ site.url | append: stripped_url | xml_escape }}
      {% if page.last_modified_at %}
        {{ page.last_modified_at | date_to_xmlschema }}
      {% else %}
        {{ page.date | date_to_xmlschema }}
      {% endif %}

  {% endunless %}
{% endfor %}
